<?php

namespace App\Tests;

use App\Image\ImageServiceInterface;
use App\Image\Upload\ImageUploadStrategyInterface;
use Symfony\Bundle\FrameworkBundle\Test\KernelTestCase;
use Symfony\Component\HttpFoundation\Request;
use Symfony\Component\Serializer\Exception\NotEncodableValueException;
use Symfony\Component\Serializer\Exception\NotNormalizableValueException;

class ImageServiceTest extends KernelTestCase
{
    public function testBase64Strategy(): void
    {
        self::bootKernel();
        $container = static::getContainer();

        $request = new Request(content: <<< JSON
{
  "items": [
    {
      "data": "data:image/png;base64,",
      "originalId": "avatar.png"
    },
    {
      "data": "data:image/png;base64,",
      "originalId": "avatar2.png"
    }
  ]
}
JSON
);
        /** @var ImageUploadStrategyInterface $strategy */
        $strategy = $container->get('image_upload.base64_strategy');
        /** @var ImageServiceInterface $imageService */
        $imageService = $container->get(ImageServiceInterface::class);

        $response = $imageService->upload($strategy, $request);

        $this->assertNotNull($response);
        $this->assertCount(2, $response->items);

        $this->assertEquals('avatar.png', $response->items[0]->id);
        $this->assertEquals('avatar2.png', $response->items[1]->id);
    }

    public function testBase64StrategyWrongData(): void
    {
        $expectedException = new NotNormalizableValueException('The provided "data:" URI is not valid.');
        $this->expectExceptionObject($expectedException);
        $this->expectExceptionMessage($expectedException->getMessage());

        self::bootKernel();
        $container = static::getContainer();

        $request = new Request(content: <<< JSON
{
  "items": [
    {
      "data": "wrong_data",
      "originalId": "test.png"
    }
  ]
}
JSON
        );
        /** @var ImageUploadStrategyInterface $strategy */
        $strategy = $container->get('image_upload.base64_strategy');
        /** @var ImageServiceInterface $imageService */
        $imageService = $container->get(ImageServiceInterface::class);

        $imageService->upload($strategy, $request);
    }

    public function testBase64StrategyMalformedJson(): void
    {
        $expectedException = new NotEncodableValueException('Syntax error');
        $this->expectExceptionObject($expectedException);
        $this->expectExceptionMessage($expectedException->getMessage());

        self::bootKernel();
        $container = static::getContainer();

        $request = new Request(content: <<< JSON
{JSON_ERROR
  "items": [
    {
      "data": "data:",
      "originalId": "test.png"
    }
  ]
}
JSON
        );
        /** @var ImageUploadStrategyInterface $strategy */
        $strategy = $container->get('image_upload.base64_strategy');
        /** @var ImageServiceInterface $imageService */
        $imageService = $container->get(ImageServiceInterface::class);

        $imageService->upload($strategy, $request);
    }
}
